class BinaryMatrix {
  private let matrix: [[Int]]
  
  init(matrix: [[Int]]) {
    self.matrix = matrix
  }
  
  func get(_ row: Int, _ col: Int) -> Int {
    return matrix[row][col]
  }
  
  func dimensions() -> [Int] {
    return [matrix.count, matrix[0].count]
  }
}

class Solution {
  func leftMostColumnWithOne_best(_ binaryMatrix: BinaryMatrix) -> Int {
    let dimensions = binaryMatrix.dimensions()
    let rows = dimensions[0]
    let cols = dimensions[1]
    
    var row = 0
    var col = cols - 1
    
    while row < rows && col >= 0 {
      if binaryMatrix.get(row, col) == 1 {
        col -= 1
      } else {
        row += 1
      }
    }
    
    let notFound = col == cols - 1
    
    return notFound ? -1 : col + 1
  }
  
  func leftMostColumnWithOne_binarySearch(_ binaryMatrix: BinaryMatrix) -> Int {
    let dimensions = binaryMatrix.dimensions()
    let rows = dimensions[0]
    let cols = dimensions[1]
    
    var result = Int.max
    
    for row in 0..<rows {
      result = min(binarySearch(binaryMatrix, row: row, cols: cols), result)
      
      if result == 0 {
        break
      }
    }
    
    
    return result == Int.max ? -1 : result
  }
  
  private func binarySearch(_ binaryMatrix: BinaryMatrix, row: Int, cols: Int) -> Int {
    var start = 0
    var end = cols - 1
    
    var index = Int.max
    
    while start <= end {
      let mid = start + (end - start)/2
      
      if binaryMatrix.get(row, mid) == 1 {
        index = mid
        end = mid - 1
      } else {
        start = mid + 1
      }
    }
    
    return index
  }
}


class TestSolution: XCTestCase {
  var solution: Solution!
  
  override func setUp() {
    super.setUp()
    solution = Solution()
  }
  
  func test1_BS() {
    let expected = 1
    let binaryMatrix = BinaryMatrix(matrix: [[0,0],[0,1]])
    let actual = solution.leftMostColumnWithOne_binarySearch(binaryMatrix)

    assert(expected == actual)
  }
  
  func test1_best() {
    let expected = 1
    let binaryMatrix = BinaryMatrix(matrix: [[0,0],[0,1]])
    let actual = solution.leftMostColumnWithOne_best(binaryMatrix)

    assert(expected == actual)
  }
  
  func test2_BS() {
    let expected = 41
    let binaryMatrix = BinaryMatrix(matrix: bigMatrix)
    let actual = solution.leftMostColumnWithOne_binarySearch(binaryMatrix)

    assert(expected == actual)
  }
  
  func test2_best() {
    let expected = 41
    let binaryMatrix = BinaryMatrix(matrix: bigMatrix)
    let actual = solution.leftMostColumnWithOne_best(binaryMatrix)

    assert(expected == actual)
  }
  
}

class TestObserver: NSObject, XCTestObservation {
    func testCase(_ testCase: XCTestCase,
                  didFailWithDescription description: String,
                  inFile filePath: String?,
                  atLine lineNumber: Int) {
        assertionFailure(description, line: UInt(lineNumber))
    }
}

let testObserver = TestObserver()
XCTestObservationCenter.shared.addTestObserver(testObserver)
TestSolution.defaultTestSuite.run()
